import React, { useState } from "react";
import axios from "axios";

const AdicionarLivro = ({ onLivroAdicionado }) => {
    const [titulo, setTitulo] = useState("");
    const [autor, setAutor] = useState("");
    const [anoPublicacao, setAnoPublicacao] = useState("");
    const [categoria, setCategoria] = useState("");
    const [quantidade, setQuantidade] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();
        const novoLivro = { titulo, autor, anoPublicacao, categoria, quantidade: parseInt(quantidade) };

        axios.post("http://localhost:8080/livros", novoLivro)
            .then(() => {
                alert("Livro adicionado com sucesso!");
                onLivroAdicionado();
                setTitulo("");
                setAutor("");
                setAnoPublicacao("");
                setCategoria("");
                setQuantidade("");
            })
            .catch((error) => {
                console.error("Erro ao adicionar livro:", error);
                alert("Erro ao adicionar livro. Verifique os campos.");
            });
    };

    return (
        <div className="container mt-5">
            <h3 className="text-center">Adicionar Livro</h3>
            <form onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label className="form-label">Título:</label>
                    <input
                        type="text"
                        className="form-control"
                        value={titulo}
                        onChange={(e) => setTitulo(e.target.value)}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label className="form-label">Autor:</label>
                    <input
                        type="text"
                        className="form-control"
                        value={autor}
                        onChange={(e) => setAutor(e.target.value)}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label className="form-label">Ano de Publicação:</label>
                    <input
                        type="number"
                        className="form-control"
                        value={anoPublicacao}
                        onChange={(e) => setAnoPublicacao(e.target.value)}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label className="form-label">Categoria:</label>
                    <input
                        type="text"
                        className="form-control"
                        value={categoria}
                        onChange={(e) => setCategoria(e.target.value)}
                        required
                    />
                </div>
                <div className="mb-3">
                    <label className="form-label">Quantidade:</label>
                    <input
                        type="number"
                        className="form-control"
                        value={quantidade}
                        onChange={(e) => setQuantidade(e.target.value)}
                        required
                    />
                </div>
                <button type="submit" className="btn btn-success">
                    Adicionar Livro
                </button>
            </form>
        </div>
    );
};

export default AdicionarLivro;
