import React, { useState, useEffect } from "react";
import axios from "axios";

const EditarLivro = ({ livro, onLivroEditado, onCancelar }) => {
    const [titulo, setTitulo] = useState("");
    const [autor, setAutor] = useState("");
    const [anoPublicacao, setAnoPublicacao] = useState("");
    const [categoria, setCategoria] = useState("");
    const [quantidade, setQuantidade] = useState("");

    useEffect(() => {
        // Preenche os campos com os dados do livro selecionado
        if (livro) {
            setTitulo(livro.titulo);
            setAutor(livro.autor);
            setAnoPublicacao(livro.anoPublicacao);
            setCategoria(livro.categoria);
            setQuantidade(livro.quantidade);
        }
    }, [livro]);

    const handleSubmit = (e) => {
        e.preventDefault();
        const livroEditado = { titulo, autor, anoPublicacao, categoria, quantidade: parseInt(quantidade) };

        axios.put(`http://localhost:8080/livros/${livro.id}`, livroEditado)
            .then(() => {
                alert("Livro atualizado com sucesso!");
                onLivroEditado();
            })
            .catch((error) => {
                console.error("Erro ao editar livro:", error);
                alert("Erro ao editar livro. Verifique os campos.");
            });
    };

    return (
        <form onSubmit={handleSubmit}>
            <h3>Editar Livro</h3>
            <div>
                <label>Título:</label>
                <input type="text" value={titulo} onChange={(e) => setTitulo(e.target.value)} required />
            </div>
            <div>
                <label>Autor:</label>
                <input type="text" value={autor} onChange={(e) => setAutor(e.target.value)} required />
            </div>
            <div>
                <label>Ano de Publicação:</label>
                <input type="number" value={anoPublicacao} onChange={(e) => setAnoPublicacao(e.target.value)} required />
            </div>
            <div>
                <label>Categoria:</label>
                <input type="text" value={categoria} onChange={(e) => setCategoria(e.target.value)} required />
            </div>
            <div>
                <label>Quantidade:</label>
                <input type="number" value={quantidade} onChange={(e) => setQuantidade(e.target.value)} required />
            </div>
            <button type="submit">Salvar Alterações</button>
            <button type="button" onClick={onCancelar}>Cancelar</button>
        </form>
    );
};

export default EditarLivro;
