import React, { useEffect, useState } from "react";
import axios from "axios";
import EditarLivro from "./EditarLivro";

const LivroList = ({ reload }) => {
    const [livros, setLivros] = useState([]);
    const [livroSelecionado, setLivroSelecionado] = useState(null);

    useEffect(() => {
        axios.get("http://localhost:8080/livros")
            .then(response => setLivros(response.data))
            .catch(error => console.error("Erro ao buscar livros:", error));
    }, [reload]);

    const handleEditarClick = (livro) => {
        setLivroSelecionado(livro);
    };

    const handleLivroEditado = () => {
        setLivroSelecionado(null);
        axios.get("http://localhost:8080/livros")
            .then(response => setLivros(response.data));
    };

    const handleCancelar = () => {
        setLivroSelecionado(null);
    };

    const handleExcluirClick = (id) => {
        if (window.confirm("Tem certeza que deseja excluir este livro?")) {
            axios.delete(`http://localhost:8080/livros/${id}`)
                .then(() => {
                    alert("Livro excluído com sucesso!");
                    setLivros(livros.filter(livro => livro.id !== id));
                })
                .catch(error => {
                    console.error("Erro ao excluir livro:", error);
                    alert("Erro ao excluir livro.");
                });
        }
    };

    return (
        <div className="container mt-5">
            <h2 className="text-center">Lista de Livros</h2>
            {livroSelecionado ? (
                <EditarLivro
                    livro={livroSelecionado}
                    onLivroEditado={handleLivroEditado}
                    onCancelar={handleCancelar}
                />
            ) : (
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Título</th>
                            <th>Autor</th>
                            <th>Ano de Publicação</th>
                            <th>Categoria</th>
                            <th>Quantidade</th>
                            <th>Ações</th>
                        </tr>
                    </thead>
                    <tbody>
                        {livros.map((livro) => (
                            <tr key={livro.id}>
                                <td>{livro.id}</td>
                                <td>{livro.titulo}</td>
                                <td>{livro.autor}</td>
                                <td>{livro.anoPublicacao}</td>
                                <td>{livro.categoria}</td>
                                <td>{livro.quantidade}</td>
                                <td>
                                    <button
                                        className="btn btn-primary btn-sm me-2"
                                        onClick={() => handleEditarClick(livro)}
                                    >
                                        Editar
                                    </button>
                                    <button
                                        className="btn btn-danger btn-sm"
                                        onClick={() => handleExcluirClick(livro.id)}
                                    >
                                        Excluir
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default LivroList;
