import React, { useState } from "react";
import axios from "axios";

const AdicionarLivro = ({ onLivroAdicionado }) => {
    const [titulo, setTitulo] = useState("");
    const [autor, setAutor] = useState("");
    const [anoPublicacao, setAnoPublicacao] = useState("");
    const [categoria, setCategoria] = useState("");
    const [quantidade, setQuantidade] = useState("");

    const handleSubmit = (e) => {
        e.preventDefault();
        const novoLivro = { titulo, autor, anoPublicacao, categoria, quantidade: parseInt(quantidade) };

        axios.post("http://localhost:8080/livros", novoLivro)
            .then(() => {
                alert("Livro adicionado com sucesso!");
                onLivroAdicionado();
                setTitulo("");
                setAutor("");
                setAnoPublicacao("");
                setCategoria("");
                setQuantidade("");
            })
            .catch((error) => {
                console.error("Erro ao adicionar livro:", error);
                alert("Erro ao adicionar livro. Verifique os campos.");
            });
    };

    return (
        <form onSubmit={handleSubmit}>
            <h3>Adicionar Livro</h3>
            <div>
                <label>Título:</label>
                <input type="text" value={titulo} onChange={(e) => setTitulo(e.target.value)} required />
            </div>
            <div>
                <label>Autor:</label>
                <input type="text" value={autor} onChange={(e) => setAutor(e.target.value)} required />
            </div>
            <div>
                <label>Ano de Publicação:</label>
                <input type="number" value={anoPublicacao} onChange={(e) => setAnoPublicacao(e.target.value)} required />
            </div>
            <div>
                <label>Categoria:</label>
                <input type="text" value={categoria} onChange={(e) => setCategoria(e.target.value)} required />
            </div>
            <div>
                <label>Quantidade:</label>
                <input type="number" value={quantidade} onChange={(e) => setQuantidade(e.target.value)} required />
            </div>
            <button type="submit">Adicionar</button>
        </form>
    );
};

export default AdicionarLivro;
